const resp = variables['4fb8272b-2831-4e5d-ad45-aafec9bb67f2'];
const data = resp?.overview?.series || [];
const gran = resp?.overview?.axes?.granularity || 'day';
const monthsRU = ['янв','фев','мар','апр','май','июн','июл','авг','сен','окт','ноя','дек'];

function fmtDate(d) {
  const dd = String(d.getDate()).padStart(2, '0');
  const mm = monthsRU[d.getMonth()];
  const yyyy = d.getFullYear();
  return `${dd} ${mm} ${yyyy}`;
}
function fmtDateShort(d) {
  const dd = String(d.getDate()).padStart(2, '0');
  const mm = monthsRU[d.getMonth()];
  return `${dd} ${mm}`;
}

return data.map(s => {
  const start = new Date(s.ts);
  const end = new Date(start);
  if (gran === 'hour') {
    end.setHours(end.getHours() + 1);
    return `${String(start.getHours()).padStart(2,'0')}:00`;
  }
  if (gran === 'day')  return fmtDate(start);
  if (gran === 'week') {
    end.setDate(end.getDate() + 6);
    return start.getMonth() === end.getMonth()
      ? `${fmtDateShort(start)}–${String(end.getDate()).padStart(2,'0')} ${monthsRU[end.getMonth()]}`
      : `${fmtDateShort(start)}–${fmtDateShort(end)}`;
  }
  if (gran === 'month') return `${monthsRU[start.getMonth()]} ${start.getFullYear()}`;
  return s.ts_label || '';
});

