const monthsRU = ['янв','фев','мар','апр','май','июн','июл','авг','сен','окт','ноя','дек'];

function fmtDate(d) {
  const dd = String(d.getDate()).padStart(2, '0');
  const mm = monthsRU[d.getMonth()];
  const yyyy = d.getFullYear();
  return `${dd} ${mm} ${yyyy}`;
}
function fmtDateShort(d) {
  const dd = String(d.getDate()).padStart(2, '0');
  const mm = monthsRU[d.getMonth()];
  return `${dd} ${mm}`;
}

return {
  plugins: {
    tooltip: {
      callbacks: {
        title(items) {
          const i = items?.[0]?.dataIndex ?? 0;
          const s = data[i];
          if (!s) return '';
          // s.ts — начало бакета в ISO (UTC); возьмём локально
          const start = new Date(s.ts);
          const end = new Date(start);

          if (gran === 'hour') {
            end.setHours(end.getHours() + 1);
            return `${fmtDate(start)} ${String(start.getHours()).padStart(2,'0')}:00–${String(end.getHours()).padStart(2,'0')}:00`;
          }
          if (gran === 'day') {
            return fmtDate(start);
          }
          if (gran === 'week') {
            end.setDate(end.getDate() + 6);
            // «07–13 апр 2025»
            if (start.getMonth() === end.getMonth()) {
              return `${fmtDateShort(start)}–${String(end.getDate()).padStart(2,'0')} ${monthsRU[end.getMonth()]} ${end.getFullYear()}`;
            } else {
              return `${fmtDateShort(start)}–${fmtDate(end)}`;
            }
          }
          if (gran === 'month') {
            return `${monthsRU[start.getMonth()]} ${start.getFullYear()}`;
          }
          return s.ts_label || '';
        }
      }
    },
    legend: { display: true }
  },
  scales: {
    x: {
      ticks: { maxTicksLimit: resp?.overview?.axes?.x_tick_count || 12 }
    },
    y: {
      beginAtZero: true,
      title: { display: true, text: 'м³' }
    }
  }
};

