return (() => {
  const state = variables['e8a56673-ded0-47c4-8335-c41ed6f64845'] || {};
  // Бэкенд иногда кладёт total_steps в date.total_steps — учитываем оба варианта
  const total = state?.date?.total_steps ?? state?.total_steps ?? 0;
  // Текущий шаг (1-based) с защитой от выхода за границы
  const current = Math.min(Math.max(1, state?.step ?? 1), Math.max(1, total));

  if (total <= 0) return [];

  // Малые наборы — показываем всё подряд
  if (total <= 7) {
    return Array.from({ length: total }, (_, i) => ({
      type: 'page',
      step: i + 1,
      label: String(i + 1),
      active: i + 1 === current,
      disabled: false
    }));
  }

  // Для больших наборов: первые 2, окно вокруг current (±2), последние 2
  const want = new Set([1, 2, total - 1, total,
                        current - 2, current - 1, current, current + 1, current + 2]
                        .filter(n => n >= 1 && n <= total));

  const sorted = Array.from(want).sort((a,b) => a - b);

  // Склеиваем с "…", если есть разрыв > 1
  const out = [];
  for (let i = 0; i < sorted.length; i++) {
    const n = sorted[i];
    const prev = sorted[i - 1];

    if (i > 0 && n - prev > 1) {
      out.push({ type: 'ellipsis', label: '…', disabled: true });
    }

    out.push({
      type: 'page',
      step: n,
      label: String(n),
      active: n === current,
      disabled: false
    });
  }

  return out;
})()

