const hm = variables['4fb8272b-2831-4e5d-ad45-aafec9bb67f2']?.overview?.heatmap || [];
const rawDays = [...new Set(hm.map(d => d.day))];
const days = rawDays.map(d => {
  const dt = new Date(d);
  return dt.toLocaleDateString('ru-RU', { day: '2-digit', month: '2-digit' });
});
const hours = Array.from({ length: 24 }, (_, h) => String(h).padStart(2, '0'));

const tileSizer = {
  id: 'tileSizer',
  beforeLayout(chart) {
    const ds = chart.data.datasets?.[0];
    const area = chart.chartArea;
    if (!ds || !area) return;
    const cols = hours.length;
    const rows = Math.max(1, days.length);
    const gutter = 2;
    const cellW = Math.max(6, Math.floor(area.width  / cols) - gutter);
    const cellH = Math.max(6, Math.floor(area.height / rows) - gutter);
    const r = Math.floor(Math.min(cellW, cellH) / 2);
    ds.pointRadius = r;
    ds.pointHoverRadius = r;
    ds.borderWidth = Math.max(1, Math.floor(r * 0.25));
  }
};

return {
  plugins: {
    legend: { display: false },
    tooltip: {
      callbacks: {
        title() { return ''; },
        label(ctx) {
          const { x, y, v, rawDay } = ctx.raw || {};
          const val = (v == null) ? '—' : `${v.toFixed(2)}%`;
          return `${rawDay} ${x}:00 — ${val}`;
        }
      }
    }
  },
  scales: {
    x: {
      type: 'category',
      labels: hours,
      title: { display: true, text: 'Часы суток' },
      grid: { display: false },
      ticks: { autoSkip: false, maxRotation: 0 }
    },
    y: {
      type: 'category',
      labels: days,
      reverse: true,
      title: { display: true, text: 'Дни' },
      grid: { display: false }
    }
  },
  elements: { point: { borderJoinStyle: 'round' } },
  animation: false,
  maintainAspectRatio: false,
  plugins: [tileSizer]
};

