const { days, hours, points } = (() => {
  const hm = variables['4fb8272b-2831-4e5d-ad45-aafec9bb67f2']?.overview?.heatmap || [];
  const rawDays = [...new Set(hm.map(d => d.day))];
  const days = rawDays.map(d => {
    const dt = new Date(d);
    return dt.toLocaleDateString('ru-RU', { day: '2-digit', month: '2-digit' });
  });
  const hours = Array.from({ length: 24 }, (_, h) => String(h).padStart(2, '0'));
  const points = hm.map(d => {
    const dt = new Date(d.day);
    const yLabel = dt.toLocaleDateString('ru-RU', { day: '2-digit', month: '2-digit' });
    return { x: String(d.hour).padStart(2, '0'), y: yLabel, v: d.value, rawDay: d.day };
  });
  return { days, hours, points };
})();

function colorFor(v) {
  if (v == null) return '#E5E7EB';
  const t = Math.max(0, Math.min(1, v / 30));
  if (t < 0.5) {
    const k = t / 0.5;
    return `hsl(${150 - 60*k} 70% 45%)`;
  } else {
    const k = (t - 0.5) / 0.5;
    return `hsl(${90 - 40*k} 80% 50%)`;
  }
}

return [{
  label: 'Отклонение, %',
  data: points,
  parsing: true,
  showLine: false,
  pointStyle: 'rectRounded',
  pointRadius: 8,
  pointHoverRadius: 8,
  borderWidth: 2,
  borderColor: '#FFFFFF',
  pointBackgroundColor(ctx) {
    const v = ctx.raw?.v;
    return colorFor(v);
  }
}];
